package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import jakarta.persistence.*;import lombok.*;import java.util.List;@Getter@Setter@ToString@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_role")public class Role {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "role_id")    private int roleId;    @Column(name = "role_name")    private String roleName;    @Column(name = "role_desc")    private String roleDescription;    @OneToMany(mappedBy = "role")    @JsonIgnore    private List<User> user;}