package org.course.management.entity;import com.fasterxml.jackson.annotation.JsonIgnore;import com.fasterxml.jackson.annotation.JsonIgnoreProperties;import jakarta.persistence.*;import lombok.AllArgsConstructor;import lombok.Getter;import lombok.NoArgsConstructor;import lombok.Setter;import java.io.Serializable;import java.sql.Date;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Entity@Table(name = "cm_user")public class User implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "user_id")    private int userId;    @Column(name = "user_name")    private String username;    @Column(name = "password")    private String password;    @Column(name = "full_name", columnDefinition = "nvarchar(512)")    private String fullName;    @Column(name = "avatar")    private String avatar;    @Column(name = "email")    private String email;    @Column(name = "gender")    private boolean gender;    @Column(name = "birthday")    private Date birthday;    @Column(name = "phone_number")    private String phoneNumber;    @Column(name = "registration_date")    private Date registrationDate;    @Column(name = "is_active")    private boolean isActive;    @ManyToOne(fetch = FetchType.LAZY)    @JoinColumn(name = "role_id", referencedColumnName = "role_id")    @JsonIgnoreProperties(value = {"applications", "hibernateLazyInitializer"})    private Role role;    @OneToMany(mappedBy = "user")    @JsonIgnore    private List<Enroll> enrolls;}