package org.course.management.repository;import org.course.management.entity.Role;import org.springframework.data.jpa.repository.JpaRepository;import org.springframework.data.jpa.repository.Query;import java.util.List;public interface RoleRepository extends JpaRepository<Role, Integer> {    @Query("SELECT r FROM Role r WHERE r.roleName LIKE?1")    List<Role> findByRoleName(String roleName);}