package org.course.management.service.impl;import org.course.management.entity.CourseDetail;import org.course.management.repository.CourseDetailRepository;import org.course.management.service.CourseDetailService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class CourseDetailServiceImpl implements CourseDetailService {    @Autowired    private CourseDetailRepository courseDetailRepository;    @Override    public List<CourseDetail> getAllCourseDetail() {        return courseDetailRepository.findAll();    }    @Override    public void doGetDeleteByIdCourseDetail(int id) {        courseDetailRepository.deleteById(id);    }    @Override    public CourseDetail doSaveCourseDetail(CourseDetail courseDetail) {        return courseDetailRepository.saveAndFlush(courseDetail);    }    @Override    public CourseDetail findById(int id) {        return courseDetailRepository.findById(id);    }}