package org.course.management.service.impl;import org.course.management.entity.Topic;import org.course.management.repository.TopicRepository;import org.course.management.service.TopicService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class TopicServiceImpl implements TopicService {    @Autowired    private TopicRepository tp;    @Override    public List<Topic> getAllTopic() {        return tp.findAll();    }    @Override    public void doGetDeleteTopic(int topicId) {        tp.deleteById(topicId);    }    @Override    public Topic doGetSaveTopic(Topic topic) {        return tp.saveAndFlush(topic);    }}